import{_ as a,c as e,ag as r,o as i}from"./chunks/framework.CCnnzLsu.js";const _=JSON.parse('{"title":"回测报告各指标计算说明","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/future_strategy/indicator_desc/"}],["meta",{"property":"og:title","content":"回测报告各指标计算说明"}]]},"headers":[],"relativePath":"future_strategy/indicator_desc/index.md","filePath":"future_strategy/indicator_desc/index.md","lastUpdated":null}'),l={name:"future_strategy/indicator_desc/index.md"};function o(n,t,h,x,d,s){return i(),e("div",null,t[0]||(t[0]=[r('<h1 id="回测报告各指标计算说明" tabindex="-1">回测报告各指标计算说明 <a class="header-anchor" href="#回测报告各指标计算说明" aria-label="Permalink to &quot;回测报告各指标计算说明&quot;">​</a></h1><h2 id="_1-测试天数为自然日" tabindex="-1">1. 测试天数为自然日 <a class="header-anchor" href="#_1-测试天数为自然日" aria-label="Permalink to &quot;1. 测试天数为自然日&quot;">​</a></h2><h2 id="_2-杠杆倍数" tabindex="-1">2. 杠杆倍数 <a class="header-anchor" href="#_2-杠杆倍数" aria-label="Permalink to &quot;2. 杠杆倍数&quot;">​</a></h2><p>\\[ \\text{杠杆倍数} = \\frac{\\text{最大资金使用率}}{\\text{保证金率}} \\]</p><ul><li>外盘保证金率计算：</li></ul><p>\\[ \\text{外盘保证金率} = \\frac{\\text{每手保证金} \\times \\text{开仓总手数}}{\\text{开仓成交额}} \\]</p><h2 id="_3-概念定义" tabindex="-1">3. 概念定义 <a class="header-anchor" href="#_3-概念定义" aria-label="Permalink to &quot;3. 概念定义&quot;">​</a></h2><ul><li><strong>本金</strong> = 初始资金 + 出入金</li><li><strong>盈亏</strong> = 平仓盈亏 + 浮动盈亏</li><li><strong>收益</strong> = 平仓盈亏 + 浮动盈亏 - 手续费</li><li><strong>权益</strong> = 平仓盈亏 + 浮动盈亏 - 手续费 + 初始资金 + 出入金</li><li><strong>损益</strong> = 平仓盈亏 - 手续费</li></ul><h2 id="_4-收益率" tabindex="-1">4. 收益率 <a class="header-anchor" href="#_4-收益率" aria-label="Permalink to &quot;4. 收益率&quot;">​</a></h2><p>\\[ \\text{收益率} = \\frac{\\text{收益}}{\\text{本金}} \\]</p><h2 id="_5-权益离散度" tabindex="-1">5. 权益离散度 <a class="header-anchor" href="#_5-权益离散度" aria-label="Permalink to &quot;5. 权益离散度&quot;">​</a></h2><p>\\[ \\text{权益离散度} = \\sqrt{\\frac{\\text{单次盈亏的平方和}}{\\text{交易次数}}} \\]</p><h2 id="_6-权益离散系数" tabindex="-1">6. 权益离散系数 <a class="header-anchor" href="#_6-权益离散系数" aria-label="Permalink to &quot;6. 权益离散系数&quot;">​</a></h2><p>\\[ \\text{权益离散系数} = \\frac{\\text{权益离散度}}{\\text{本金}} \\times 100\\% \\]</p><h2 id="_7-平均保证金收益率" tabindex="-1">7. 平均保证金收益率 <a class="header-anchor" href="#_7-平均保证金收益率" aria-label="Permalink to &quot;7. 平均保证金收益率&quot;">​</a></h2><p>\\[ \\text{平均保证金收益率} = (\\frac{\\text{（每次交易的收益}} { \\text{开仓保证金}} ) 的平均值 \\]</p><h2 id="_8-权益最大回撤比率" tabindex="-1">8. 权益最大回撤比率 <a class="header-anchor" href="#_8-权益最大回撤比率" aria-label="Permalink to &quot;8. 权益最大回撤比率&quot;">​</a></h2><p>\\[ \\text{权益最大回撤比率} = (\\frac{\\text{（权益回撤}} { \\text{权益回撤时的权益}} ) 的最大值 \\]</p><h2 id="_9-损益最大回撤比率" tabindex="-1">9. 损益最大回撤比率 <a class="header-anchor" href="#_9-损益最大回撤比率" aria-label="Permalink to &quot;9. 损益最大回撤比率&quot;">​</a></h2><p>\\[ \\text{损益最大回撤比率} = (\\frac{\\text{损益回撤}} { \\text{损益回撤时的权益}} ) 的最大值 \\]</p><h2 id="_10-本金风险率" tabindex="-1">10. 本金风险率 <a class="header-anchor" href="#_10-本金风险率" aria-label="Permalink to &quot;10. 本金风险率&quot;">​</a></h2><p>\\[ \\text{本金风险率} = \\frac{\\text{本金} - \\text{回测期间最小权益}}{\\text{本金}} \\]</p><h2 id="_11-年化收益风险比率" tabindex="-1">11. 年化收益风险比率 <a class="header-anchor" href="#_11-年化收益风险比率" aria-label="Permalink to &quot;11. 年化收益风险比率&quot;">​</a></h2><p>\\[ \\text{年化收益风险比率} = \\frac{\\text{年化单利收益率}}{\\text{本金风险率}} \\]</p><h2 id="_12-胜率" tabindex="-1">12. 胜率 <a class="header-anchor" href="#_12-胜率" aria-label="Permalink to &quot;12. 胜率&quot;">​</a></h2><p>\\[ \\text{胜率} = \\frac{\\text{盈利次数} + \\text{持平次数}}{\\text{交易次数}} \\]</p><h2 id="_13-盈亏比率" tabindex="-1">13. 盈亏比率 <a class="header-anchor" href="#_13-盈亏比率" aria-label="Permalink to &quot;13. 盈亏比率&quot;">​</a></h2><p>\\[ \\text{盈亏比率} = \\frac{\\text{盈利交易的平均每次盈利}}{\\text{亏损交易的平均每次亏损}} \\]</p><h2 id="_14-单次最大盈利占比率" tabindex="-1">14. 单次最大盈利占比率 <a class="header-anchor" href="#_14-单次最大盈利占比率" aria-label="Permalink to &quot;14. 单次最大盈利占比率&quot;">​</a></h2><p>\\[ \\text{单次最大盈利占比率} = \\frac{\\text{单次最大盈利}}{\\text{总盈利}} \\]</p><h2 id="_15-单次最大亏损占比率" tabindex="-1">15. 单次最大亏损占比率 <a class="header-anchor" href="#_15-单次最大亏损占比率" aria-label="Permalink to &quot;15. 单次最大亏损占比率&quot;">​</a></h2><p>\\[ \\text{单次最大亏损占比率} = \\frac{\\text{单次最大亏损}}{\\text{总亏损}} \\]</p><h3 id="特别说明-逐笔对冲结算规则差异" tabindex="-1">特别说明：逐笔对冲结算规则差异 <a class="header-anchor" href="#特别说明-逐笔对冲结算规则差异" aria-label="Permalink to &quot;特别说明：逐笔对冲结算规则差异&quot;">​</a></h3><ol><li>浮动盈亏根据开仓均价计算（非昨日结算价）</li><li>开仓占用的保证金根据开仓均价计算（非昨日结算价）</li><li>资金不足不追加保证金</li><li>开仓占用保证金计算：<br> \\[ \\text{开仓占用保证金} = \\text{开仓均价} \\times \\text{交易单位} \\times \\text{手数} \\times \\text{保证金比例} ( 或 )\\text{手数} \\times \\text{每手保证金} \\]</li><li>资金使用率计算：<br> \\[ \\text{资金使用率} = \\frac{\\text{持仓保证金}}{\\text{当前权益}} \\]</li></ol>',34)]))}const u=a(l,[["render",o]]);export{_ as __pageData,u as default};
