import{_ as e}from"./chunks/new_icon.CP--apCK.js";import{_ as r,c as t,ag as i,o as l}from"./chunks/framework.CCnnzLsu.js";const d="/metaeditor/mql5_wizard.png",M=JSON.parse('{"title":"MQL4/MQL5 向导","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/metaeditor/mql5_wizard/"}],["meta",{"property":"og:title","content":"MQL4/MQL5 向导"}]]},"headers":[],"relativePath":"metaeditor/mql5_wizard/index.md","filePath":"metaeditor/mql5_wizard/index.md","lastUpdated":1744293048000}'),o={name:"metaeditor/mql5_wizard/index.md"};function _(m,a,n,p,s,c){return l(),t("div",null,a[0]||(a[0]=[i('<h1 id="mql4-mql5-向导" tabindex="-1">MQL4/MQL5 向导 <a class="header-anchor" href="#mql4-mql5-向导" aria-label="Permalink to &quot;MQL4/MQL5 向导&quot;">​</a></h1><p>MQL4/MQL5 向导允许您快速创建程序模板和成品交易机器人。</p><p>借助 MQL4/MQL5 向导，即使没有编程技能，交易者也可以开发智能交易系统。 所有这一切所需要做的是为一个EA交易选择交易信号，资金管理和追踪止损算法。EA交易代码会基于选定的参数自动生成。</p><p>此外，MQL4/MQL5 向导能够创建 MQL4/MQL5 程序模板来简化程序员的工作。 模板是一个专为编写源代码而设计的文件。在它创建之后，根据所选的程序类型，它会自动放置在适当的目录中。 在创建过程中，用户可以选择需要添加到模板的事件处理程序。 这可以减少创建程序时花费在例常任务上的时间。</p><p>向导还允许您创建 <a href="/metaeditor/mql5storage/projects">项目</a> ― 除了源代码文件（模板或成品 EA）之外，还会生成 MQPROJ 项目集文件。</p><p>若要启动 MQL4/MQL5 向导，请单击<img src="'+e+'" alt="新建"> 位于 <a href="/metaeditor/main_menu/main_menu_file">文件</a> 菜单或 <a href="/metaeditor/toolbar/toolbar_standard">工具栏</a> 里的新建。</p><p><img src="'+d+'" alt="MQL 向导"></p><p>向导工作的第一步是创建项目类型的选择：</p><ul><li><a href="./wizard_ea">EA交易(模板)</a><br> 创建EA交易的模板，为了交易更加有效，允许在金融市场上，完全自动化分析和交易活动。</li><li><a href="./wizard_ea_generate">EA交易(生成)</a><br> 生成现成的EA交易基于<a href="https://www.mql5.com/zh/docs/standardlibrary" title="标准程序库" target="_blank" rel="noreferrer">标准程序库</a> 通过为其选择交易信号，资金管理和追踪止损算法。</li><li><a href="./wizard_indicator">自定义指标</a><br> 生成模板来编写自定义技术指标为了价格动态分析。</li><li><a href="./wizard_script">脚本</a><br> 生成模板来编写一个脚本 - 任何操作一次执行的程序。</li><li><a href="./wizard_library">程序库</a><br> 生成模板来编写函数程序库，可以用于各种应用程序。</li><li><a href="./wizard_include">包括 (*.mqh)</a><br> 生成包括文件的模板。这些文件包含一些用于各种应用程序的确切函数。</li><li><a href="./wizard_class">新的类</a><br> 为类描述，生成包括文件模板。类可用于各种程序的开发过程中。</li></ul>',9)]))}const Q=r(o,[["render",_]]);export{M as __pageData,Q as default};
