import{_ as d,c as r,aI as e,o as a}from"./chunks/framework.b7dag8kZ.js";const p=JSON.parse('{"title":"热键","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/metaeditor/workspace/hotkeys"}],["meta",{"property":"og:title","content":"热键"}]]},"headers":[],"relativePath":"metaeditor/workspace/hotkeys.md","filePath":"metaeditor/workspace/hotkeys.md","lastUpdated":1744293048000}'),o={name:"metaeditor/workspace/hotkeys.md"};function l(i,t,h,n,m,f){return a(),r("div",null,t[0]||(t[0]=[e('<h1 id="热键" tabindex="-1">热键 <a class="header-anchor" href="#热键" aria-label="Permalink to &quot;热键&quot;">​</a></h1><p>热键（快捷键）是若干键和它们的组合，它们允许您快速执行命令，而无需通过菜单和控制面板。 根据 MetaEditor 界面当前处激活状态的窗口，键盘快捷键执行不同的功能。 不管焦点如何，一些快捷方式都执行某些功能。</p><p>快捷键位于 <a href="/metaeditor/workspace/source_code_windows">代码编辑</a> 窗口</p><table tabindex="0"><thead><tr><th>热键</th><th>描述</th></tr></thead><tbody><tr><td>Ctrl+Z</td><td>撤销最后的动作。</td></tr><tr><td>Ctrl+Y</td><td>重做上次撤消的动作。</td></tr><tr><td>Ctrl+X</td><td>剪切一个选定的片段以便稍后进行粘贴。</td></tr><tr><td>Ctrl+C</td><td>将选定的片段复制到剪贴板。</td></tr><tr><td>Ctrl+V</td><td>将剪切或复制的片段粘贴到当前光标位置。</td></tr><tr><td>删除</td><td>删除选定的片段。</td></tr><tr><td>Ctrl+A</td><td>选择所编辑文件的所有文本。</td></tr><tr><td>Ctrl+F</td><td>调用搜索窗口。</td></tr><tr><td>F3</td><td>查找当前搜索词条的下一个内容。</td></tr><tr><td>Shift+F3</td><td>查找当前搜索词条的前一个内容。</td></tr><tr><td>Ctrl+H</td><td>调用搜索并替换窗口。</td></tr><tr><td>Ctrl+F2</td><td>在所选行上启用/禁用 <a href="/metaeditor/development/intelligent_management#bookmarks">书签</a>，无论其当前状态如何。</td></tr><tr><td>F2</td><td>跳转到下一个书签。</td></tr><tr><td>Shift+F2</td><td>跳转到前一个书签。</td></tr><tr><td>Ctrl+Shift+F2</td><td>从当前文件中删除所有书签。</td></tr><tr><td>Ctrl+Space</td><td><a href="/metaeditor/development/intelligent_management#substitute">调用函数名备选列表</a> 用于编写程序代码时的替换。 名称的变体由输入的第一个字符决定。</td></tr><tr><td>Ctrl+Shift+Space</td><td><a href="/metaeditor/development/intelligent_management#info">显示当前函数签名</a>: 在函数中设置的参数顺序和类型，以及高亮显示当前编辑的参数。</td></tr><tr><td>F8</td><td>跳至下一个 在 <a href="/metaeditor/development/compile">编译</a> 期间检测到的 <a href="/metaeditor/workspace/toolbox#errors">错误或警告。</a></td></tr><tr><td>Shift+F8</td><td>跳至前一个 在 <a href="/metaeditor/development/compile">编译</a> 期间检测到的 <a href="/metaeditor/workspace/toolbox#errors">错误或警告。</a></td></tr><tr><td>Alt+G</td><td><a href="/metaeditor/development/intelligent_management#definition">跳转到</a> 一条声明或所选函数的定义，或变量。</td></tr><tr><td>Alt+M</td><td>打开当前程序代码中声明的 <a href="/metaeditor/development/intelligent_management#list">所有函数列表</a>。</td></tr><tr><td>Ctrl+&#39;</td><td>在每个选定行的开始处插入 &quot;//&quot; 注释。</td></tr><tr><td>Ctrl+;</td><td>在每个选定行的开始处删除 &quot;//&quot; 注释。</td></tr><tr><td>Ctrl+/</td><td>在指定位置插入单行 <a href="/metaeditor/development/intelligent_management#comments">注释</a> 符号。</td></tr><tr><td>Ctrl+.</td><td>在指定位置插入 <a href="/metaeditor/development/intelligent_management#comments">注释</a> 工件至函数。</td></tr><tr><td>Ctrl+,</td><td>启动 <a href="/metaeditor/development/styler">格式化</a> 为当前编辑文件的代码排版。</td></tr><tr><td>Ctrl+0-9</td><td>设置命名 <a href="/metaeditor/development/intelligent_management#bookmarks">书签</a>。</td></tr><tr><td>Alt+0-9</td><td>进入到命名书签。</td></tr><tr><td>Ctrl+- 或 Alt+左方向键</td><td>转到光标所在的前一行。 这只能在当前打开的文件中使用。</td></tr><tr><td>Ctrl+Shift+- 或 Alt+右方向键</td><td>从上一行返回到下一行。这只能在当前打开的文件中使用。</td></tr><tr><td>Tab</td><td>在所选行的开头插入三个空格（制表符）。</td></tr><tr><td>Tab+Shift</td><td>在所选行的开头删除三个空格（制表符号）。</td></tr><tr><td>Ctrl+Shift+U</td><td>将所选短语的字符转换为大写。</td></tr><tr><td>Ctrl+U</td><td>将所选短语的字符转换为下写。</td></tr><tr><td>Ctrl+Tab</td><td>切换到前一个打开的代码编辑窗口。</td></tr><tr><td>Ctrl+Shift+Tab</td><td>切换到下一个打开的代码编辑窗口。</td></tr></tbody></table><h2 id="用于-调试的热键" tabindex="-1">用于 <a href="/metaeditor/development/debug">调试的热键</a> <a class="header-anchor" href="#用于-调试的热键" aria-label="Permalink to &quot;用于 [调试的热键](/metaeditor/development/debug)&quot;">​</a></h2><table tabindex="0"><thead><tr><th>热键</th><th>描述</th></tr></thead><tbody><tr><td>F5</td><td>开始 <a href="/metaeditor/development/debug">调试</a> 当前所选程序。</td></tr><tr><td>中断</td><td>暂停调试。</td></tr><tr><td>Shift+F5</td><td>停止调试。</td></tr><tr><td>F11</td><td>步入: 程序执行移动一步，进入被调用函数。</td></tr><tr><td>F10</td><td>步跃: 程序执行移动一步，但不进入被调用函数。</td></tr><tr><td>Shift+F11</td><td>步出: 在调用堆栈的高一层单步执行程序。</td></tr><tr><td>F9</td><td>根据当前状态启用/禁用当前行上的断点。</td></tr><tr><td>Shift+F9</td><td>将程序代码中高亮显示的表达式添加到观察中。</td></tr></tbody></table><h2 id="一般的-metaeditor-命令" tabindex="-1">一般的 MetaEditor 命令 <a class="header-anchor" href="#一般的-metaeditor-命令" aria-label="Permalink to &quot;一般的 MetaEditor 命令&quot;">​</a></h2><table tabindex="0"><thead><tr><th>热键</th><th>描述</th></tr></thead><tbody><tr><td>Ctrl+N</td><td>打开 <a href="/metaeditor/mql5_wizard/">MQL4/MQL5 向导</a>。</td></tr><tr><td>Ctrl+O</td><td>打开一个文件。</td></tr><tr><td>Ctrl+S</td><td>保存当前所编辑文件中的更改。</td></tr><tr><td>Ctrl+F4</td><td>关闭当前编辑的文件。</td></tr><tr><td>F7</td><td><a href="/metaeditor/development/compile">编译</a> 当前所编辑文件。</td></tr><tr><td>Ctrl+F7</td><td>编译当前所有打开进行编辑的文件。</td></tr><tr><td>Ctrl+Break</td><td>取消编译。</td></tr><tr><td>Ctrl+P</td><td>打开当前文件打印窗口。</td></tr><tr><td>Ctrl+Shift+F</td><td>打开窗口<a href="/metaeditor/development/source_code_find#find_in_files">搜索文件</a>。</td></tr><tr><td>Ctrl+D</td><td>显示/隐藏 <a href="/metaeditor/workspace/navigator">导航器</a> 窗口。</td></tr><tr><td>Ctrl+T</td><td>显示/隐藏 <a href="./toolbox">工具箱</a> 窗口。</td></tr><tr><td>F4</td><td>切换到交易平台。</td></tr><tr><td>F1</td><td>打开 MetaEditor 参考。</td></tr><tr><td>Ctrl+F1</td><td><a href="/metaeditor/main_menu/main_menu_help">搜索</a> 整个 <a href="https://www.mql4.com" title="MQL4 社区" target="_blank" rel="noreferrer">MQL4 社区</a>/<a href="https://www.mql5.com" target="_blank" rel="noreferrer">MQL5 社区</a> 网站。</td></tr><tr><td>F12</td><td>启用/关闭全屏模式。</td></tr></tbody></table><p><a href="/metaeditor/workspace/navigator">导航器</a> 窗口热键</p><table tabindex="0"><thead><tr><th>热键</th><th>描述</th></tr></thead><tbody><tr><td>步进</td><td>打开选择文件。</td></tr><tr><td>插入</td><td>在当前选定的文件夹中创建一个文件夹。</td></tr><tr><td>F2</td><td>为选定的文件或文件夹重命名。</td></tr><tr><td>删除</td><td>删除选定的文件或文件夹。</td></tr><tr><td>F5</td><td>更新导航器窗口。</td></tr></tbody></table><p><a href="./toolbox">工具箱</a> 窗口热键</p><table tabindex="0"><thead><tr><th>热键</th><th>描述</th></tr></thead><tbody><tr><td>步进</td><td>跳转到行, 检测到 <a href="/metaeditor/workspace/toolbox#errors">错误</a> 或 <a href="/metaeditor/development/source_code_find#find_in_files">多文件搜索</a> 的元素, 查看选择的 <a href="/metaeditor/workspace/toolbox#articles">文章</a> 或 <a href="/metaeditor/workspace/toolbox#codebase">MQL4/MQL5 程序</a>。</td></tr><tr><td>Ctrl+C</td><td>将错误行复制到剪贴板。</td></tr><tr><td>F2</td><td>在观察部分的 <a href="/metaeditor/development/debug#watch">调试</a> 选卡里编辑一条所观察表达式的名称。</td></tr><tr><td>插入</td><td>在观察部分的 <a href="/metaeditor/development/debug#watch">调试</a> 选卡里添加一条新表达式。</td></tr><tr><td>删除</td><td>在观察部分的 <a href="/metaeditor/development/debug#watch">调试</a> 选卡里删除一条选定的表达式。</td></tr><tr><td>D</td><td>从 <a href="/metaeditor/workspace/toolbox#codebase">代码库</a> 里下载一个选定的 MQL4/MQL5 程序。</td></tr><tr><td>R</td><td>显示/隐藏 <a href="/metaeditor/workspace/toolbox#articles">文章类别</a>。</td></tr><tr><td>G</td><td>显示/隐藏网格分隔域。</td></tr><tr><td>A</td><td>启用/禁用列大小自动选择。</td></tr><tr><td>S</td><td>启用/禁用 <a href="/metaeditor/workspace/toolbox#errors">错误</a> 选卡的消息自动滚动。</td></tr></tbody></table>',12)]))}const c=d(o,[["render",l]]);export{p as __pageData,c as default};
