import{_ as a,c as r,aI as o,o as e}from"./chunks/framework.b7dag8kZ.js";const u=JSON.parse('{"title":"回测及参数优化：评价函数之相关系数R平方","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/mt5_quick_start/advance_konwledge/correlation_factor"}],["meta",{"property":"og:title","content":"回测及参数优化：评价函数之相关系数R平方"}]]},"headers":[],"relativePath":"mt5_quick_start/advance_konwledge/correlation_factor.md","filePath":"mt5_quick_start/advance_konwledge/correlation_factor.md","lastUpdated":1745411614000}'),n={name:"mt5_quick_start/advance_konwledge/correlation_factor.md"};function i(l,t,s,c,_,d){return e(),r("div",null,t[0]||(t[0]=[o('<h1 id="回测及参数优化-评价函数之相关系数r平方" tabindex="-1">回测及参数优化：评价函数之相关系数R平方 <a class="header-anchor" href="#回测及参数优化-评价函数之相关系数r平方" aria-label="Permalink to &quot;回测及参数优化：评价函数之相关系数R平方&quot;">​</a></h1><h2 id="一、常见评价函数概述" tabindex="-1">一、常见评价函数概述 <a class="header-anchor" href="#一、常见评价函数概述" aria-label="Permalink to &quot;一、常见评价函数概述&quot;">​</a></h2><p>在量化交易中，评价函数是度量和比较不同交易策略或参数性能的关键工具。以下是几种常见的评价指标：</p><h3 id="_1-最大结余" tabindex="-1">1. <strong>最大结余</strong> <a class="header-anchor" href="#_1-最大结余" aria-label="Permalink to &quot;1. **最大结余**&quot;">​</a></h3><ul><li><strong>含义</strong>：简单粗暴地衡量策略的总盈利，未考虑风险。</li><li><strong>应用</strong>：参数优化时按盈利排序，盈利最高的参数排在前位。</li></ul><h3 id="_2-最大回撤" tabindex="-1">2. <strong>最大回撤</strong> <a class="header-anchor" href="#_2-最大回撤" aria-label="Permalink to &quot;2. **最大回撤**&quot;">​</a></h3><ul><li><strong>含义</strong>：净值从最高点到最低点的最大跌幅，反映策略的风险程度，越小越好。</li><li><strong>局限</strong>：仅关注风险，未结合收益，评价片面。</li></ul><h3 id="_3-盈利因子-profit-factor" tabindex="-1">3. <strong>盈利因子（Profit Factor）</strong> <a class="header-anchor" href="#_3-盈利因子-profit-factor" aria-label="Permalink to &quot;3. **盈利因子（Profit Factor）**&quot;">​</a></h3><ul><li><strong>计算</strong>：正向收益总和 / 负向收益总和（毛利 / 毛损）。</li><li><strong>意义</strong>：大于1时表明盈利超过亏损，综合衡量收益与风险，实用性强。</li></ul><h3 id="_4-风暴比-sortino-ratio-原文称-采收率" tabindex="-1">4. <strong>风暴比（Sortino Ratio，原文称“采收率”）</strong> <a class="header-anchor" href="#_4-风暴比-sortino-ratio-原文称-采收率" aria-label="Permalink to &quot;4. **风暴比（Sortino Ratio，原文称“采收率”）**&quot;">​</a></h3><ul><li><strong>计算</strong>：净盈利 / 最大净值亏损。</li><li><strong>意义</strong>：衡量单位风险下的报酬，例如按年回测时，风暴比＞0.5视为及格，长期回测需更高标准。</li></ul><h3 id="其他指标" tabindex="-1">其他指标 <a class="header-anchor" href="#其他指标" aria-label="Permalink to &quot;其他指标&quot;">​</a></h3><p>夏普比率、期望值、Z分数、年回报率等，可根据需求选择，但核心优质参数通常在多种指标中表现优异。</p>',13)]))}const g=a(n,[["render",i]]);export{u as __pageData,g as default};
