import{_ as t,c as l,ag as r,o as e}from"./chunks/framework.CCnnzLsu.js";const o="/assets/4.B184uwCF.png",i="/assets/5.DOOLEmxv.png",m=JSON.parse('{"title":"市场扫描","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/mt5_quick_start/advance_konwledge/market_scan"}],["meta",{"property":"og:title","content":"市场扫描"}]]},"headers":[],"relativePath":"mt5_quick_start/advance_konwledge/market_scan.md","filePath":"mt5_quick_start/advance_konwledge/market_scan.md","lastUpdated":1745411614000}'),n={name:"mt5_quick_start/advance_konwledge/market_scan.md"};function s(h,a,d,c,_,g){return e(),l("div",null,a[0]||(a[0]=[r('<h1 id="市场扫描" tabindex="-1">市场扫描 <a class="header-anchor" href="#市场扫描" aria-label="Permalink to &quot;市场扫描&quot;">​</a></h1><h2 id="一、市场扫描功能概述" tabindex="-1">一、市场扫描功能概述 <a class="header-anchor" href="#一、市场扫描功能概述" aria-label="Permalink to &quot;一、市场扫描功能概述&quot;">​</a></h2><p><strong>核心目标</strong>：</p><ol><li><strong>筛选适配品种</strong>：通过同一组参数在多品种上回测，找到策略表现最佳的交易品种。</li><li><strong>验证普适性</strong>：评估策略在不同市场环境（趋势/震荡/高波动）下的盈利能力，降低单一品种依赖风险。</li></ol><p><strong>适用场景</strong>：</p><ul><li>新策略初步开发时，快速定位优势品种</li><li>优化现有策略，扩展多品种组合</li><li>验证策略逻辑的市场通用性</li></ul><h2 id="二、操作步骤-mt5市场扫描实战" tabindex="-1">二、操作步骤：MT5市场扫描实战 <a class="header-anchor" href="#二、操作步骤-mt5市场扫描实战" aria-label="Permalink to &quot;二、操作步骤：MT5市场扫描实战&quot;">​</a></h2><h3 id="_1-基础设置" tabindex="-1">1. 基础设置 <a class="header-anchor" href="#_1-基础设置" aria-label="Permalink to &quot;1. 基础设置&quot;">​</a></h3><ol><li><strong>打开策略测试</strong>：MT5导航栏 → 查看 → 策略测试（快捷键Ctrl+R）</li><li><strong>选择模式</strong>：点击「市场扫描」图标（最右侧图标），自动勾选「优化→全部交易品种」</li><li><strong>参数配置</strong>： <ul><li><strong>时间周期</strong>：建议选择策略核心周期（如H1），兼顾回测速度与精度</li><li><strong>回测范围</strong>：推荐2-3年包含不同市场环境的数据（如2019-2021年包含疫情波动期）</li><li><strong>报价模式</strong>：默认「开盘价」（加速回测）</li></ul></li><li><strong>EA参数</strong>：使用默认参数或固定一组基准参数（不启用参数优化）</li></ol><p><img src="'+o+'" alt="市场扫描设置界面"></p><h3 id="_2-执行回测" tabindex="-1">2. 执行回测 <a class="header-anchor" href="#_2-执行回测" aria-label="Permalink to &quot;2. 执行回测&quot;">​</a></h3><ul><li>点击「开始」，MT5将自动对「市场报价」中所有已添加品种执行回测</li><li>结果显示：在「优化结果」标签页查看各品种的盈利、胜率、最大回撤等指标</li></ul><p><img src="'+i+'" alt="市场扫描设置界面"></p><h2 id="三、核心价值-品种筛选与普适性验证" tabindex="-1">三、核心价值：品种筛选与普适性验证 <a class="header-anchor" href="#三、核心价值-品种筛选与普适性验证" aria-label="Permalink to &quot;三、核心价值：品种筛选与普适性验证&quot;">​</a></h2><h3 id="_1-筛选优势品种" tabindex="-1">1. 筛选优势品种 <a class="header-anchor" href="#_1-筛选优势品种" aria-label="Permalink to &quot;1. 筛选优势品种&quot;">​</a></h3><h4 id="_2-实战案例" tabindex="-1">（2）实战案例 <a class="header-anchor" href="#_2-实战案例" aria-label="Permalink to &quot;（2）实战案例&quot;">​</a></h4><ul><li><strong>趋势策略适配品种</strong>：AUDUSD（低噪声，趋势性强）</li><li><strong>震荡策略适配品种</strong>：AUDNZD（高噪声，多空博弈频繁,适合均值回归策略）</li></ul><h3 id="_2-验证策略普适性" tabindex="-1">2. 验证策略普适性 <a class="header-anchor" href="#_2-验证策略普适性" aria-label="Permalink to &quot;2. 验证策略普适性&quot;">​</a></h3><ul><li><strong>核心逻辑</strong>：若策略在≥50%的测试品种上盈利，且不同类品种（外汇/贵金属/股指）均有正向表现，说明策略具备基础普适性。</li><li><strong>风险控制</strong>：避免过度依赖单一品种（如仅在BTCUSD盈利），通过多品种组合降低特定市场风险。</li></ul><h2 id="四、实战应用" tabindex="-1">四、实战应用 <a class="header-anchor" href="#四、实战应用" aria-label="Permalink to &quot;四、实战应用&quot;">​</a></h2><h3 id="_1-默认参数扫描" tabindex="-1">1. 默认参数扫描 <a class="header-anchor" href="#_1-默认参数扫描" aria-label="Permalink to &quot;1. 默认参数扫描&quot;">​</a></h3><ul><li><strong>目标</strong>：快速定位「天然适配品种」（未经参数优化即盈利的品种）</li><li><strong>操作</strong>： <ol><li>使用EA初始参数</li><li>回测2年随机时段</li><li>提取净利润&gt;0的品种</li></ol></li></ul><h3 id="_2-优势品种深度开发" tabindex="-1">2. 优势品种深度开发 <a class="header-anchor" href="#_2-优势品种深度开发" aria-label="Permalink to &quot;2. 优势品种深度开发&quot;">​</a></h3><ul><li><strong>目标</strong>：针对筛选出的品种添加专属过滤器</li></ul>',24)]))}const k=t(n,[["render",s]]);export{m as __pageData,k as default};
