import{_ as a,c as r,ag as e,o}from"./chunks/framework.CCnnzLsu.js";const s="/assets/1.sygfOPOR.png",n="/assets/2.CEQJdFjP.png",i="/assets/3.BdilKYEf.png",u=JSON.parse('{"title":"完整优化 vs 遗传优化","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/mt5_quick_start/advance_konwledge/optimize"}],["meta",{"property":"og:title","content":"完整优化 vs 遗传优化"}]]},"headers":[],"relativePath":"mt5_quick_start/advance_konwledge/optimize.md","filePath":"mt5_quick_start/advance_konwledge/optimize.md","lastUpdated":1745411614000}'),l={name:"mt5_quick_start/advance_konwledge/optimize.md"};function d(c,t,g,h,_,p){return o(),r("div",null,t[0]||(t[0]=[e('<h1 id="完整优化-vs-遗传优化" tabindex="-1">完整优化 vs 遗传优化 <a class="header-anchor" href="#完整优化-vs-遗传优化" aria-label="Permalink to &quot;完整优化 vs 遗传优化&quot;">​</a></h1><h2 id="一、mt5回测功能核心设置" tabindex="-1">一、MT5回测功能核心设置 <a class="header-anchor" href="#一、mt5回测功能核心设置" aria-label="Permalink to &quot;一、MT5回测功能核心设置&quot;">​</a></h2><h3 id="_1-回测模式选择" tabindex="-1">1. 回测模式选择 <a class="header-anchor" href="#_1-回测模式选择" aria-label="Permalink to &quot;1. 回测模式选择&quot;">​</a></h3><p><img src="'+s+'" alt="回测模式"></p><ul><li><strong>单一模式</strong>：固定参数回测（禁用优化）</li><li><strong>完整优化</strong>：穷举所有参数组合（需勾选参数范围）</li><li><strong>遗传优化</strong>：基于进化算法的启发式搜索</li><li><strong>市场扫描</strong>：多品种同步回测</li></ul><h2 id="二、参数优化核心算法对比" tabindex="-1">二、参数优化核心算法对比 <a class="header-anchor" href="#二、参数优化核心算法对比" aria-label="Permalink to &quot;二、参数优化核心算法对比&quot;">​</a></h2><h3 id="_1-完整优化-穷举法" tabindex="-1">1. 完整优化（穷举法） <a class="header-anchor" href="#_1-完整优化-穷举法" aria-label="Permalink to &quot;1. 完整优化（穷举法）&quot;">​</a></h3><p><strong>原理</strong>：遍历所有参数组合，计算每个组合的绩效。</p><p><strong>优缺点</strong>：<br> ✅ 确保找到全局最优解<br> ❌ 计算量随参数增加指数级增长</p><h3 id="_2-遗传优化-进化算法" tabindex="-1">2. 遗传优化（进化算法） <a class="header-anchor" href="#_2-遗传优化-进化算法" aria-label="Permalink to &quot;2. 遗传优化（进化算法）&quot;">​</a></h3><p><strong>原理</strong>：模拟「选择-交叉-变异」生物进化过程，通过种群迭代逼近最优解。<br><strong>核心步骤</strong>：</p><ol><li><strong>初始化种群</strong>：随机生成N个参数组合（如100个）</li><li><strong>适应度评估</strong>：计算每个组合的盈利因子</li><li><strong>选择</strong>：保留前20%优秀个体（如20个）</li><li><strong>交叉</strong>：优秀个体基因重组（如FastEMA=12与SlowEMA=26交叉）</li><li><strong>变异</strong>：随机修改部分参数（如SignalEMA+1）</li><li><strong>迭代</strong>：重复2-5步（如50代）</li></ol><p><strong>优缺点</strong>：<br> ✅ 速度快<br> ❌ 可能陷入局部最优</p><h2 id="选择参数" tabindex="-1">选择参数 <a class="header-anchor" href="#选择参数" aria-label="Permalink to &quot;选择参数&quot;">​</a></h2><p><img src="'+n+'" alt=""></p><h2 id="回测结果" tabindex="-1">回测结果 <a class="header-anchor" href="#回测结果" aria-label="Permalink to &quot;回测结果&quot;">​</a></h2><p><img src="'+i+'" alt=""></p>',17)]))}const b=a(l,[["render",d]]);export{u as __pageData,b as default};
