import{_ as t,c as l,aI as r,o}from"./chunks/framework.b7dag8kZ.js";const e="/assets/1.B56adJx0.png",i="/assets/2.CCk54xDk.png",n="/assets/3.DIYNtgGh.png",q=JSON.parse('{"title":"策略组合管理","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/mt5_quick_start/advance_konwledge/strategy_portfolio_manage"}],["meta",{"property":"og:title","content":"策略组合管理"}]]},"headers":[],"relativePath":"mt5_quick_start/advance_konwledge/strategy_portfolio_manage.md","filePath":"mt5_quick_start/advance_konwledge/strategy_portfolio_manage.md","lastUpdated":1745411614000}'),s={name:"mt5_quick_start/advance_konwledge/strategy_portfolio_manage.md"};function h(d,a,g,u,c,_){return o(),l("div",null,a[0]||(a[0]=[r('<h1 id="策略组合管理" tabindex="-1">策略组合管理 <a class="header-anchor" href="#策略组合管理" aria-label="Permalink to &quot;策略组合管理&quot;">​</a></h1><h2 id="一、策略下架管理-提前制定止损标准" tabindex="-1">一、策略下架管理：提前制定止损标准 <a class="header-anchor" href="#一、策略下架管理-提前制定止损标准" aria-label="Permalink to &quot;一、策略下架管理：提前制定止损标准&quot;">​</a></h2><h3 id="一-避免对策略产生执念" tabindex="-1">（一）避免对策略产生执念 <a class="header-anchor" href="#一-避免对策略产生执念" aria-label="Permalink to &quot;（一）避免对策略产生执念&quot;">​</a></h3><ul><li>市场环境不断变化，策略必然存在失效可能，需理性看待，避免因个人情感（如开发心血）而盲目坚持。</li><li><strong>关键原则</strong>：当策略不符合市场时，及时止损优于强行坚持。</li></ul><h3 id="二-常见下架标准-可单独或组合使用" tabindex="-1">（二）常见下架标准（可单独或组合使用） <a class="header-anchor" href="#二-常见下架标准-可单独或组合使用" aria-label="Permalink to &quot;（二）常见下架标准（可单独或组合使用）&quot;">​</a></h3><h4 id="_1-mdd倍数法" tabindex="-1">1. <strong>MDD倍数法</strong> <a class="header-anchor" href="#_1-mdd倍数法" aria-label="Permalink to &quot;1. **MDD倍数法**&quot;">​</a></h4><ul><li><strong>操作逻辑</strong>：基于回测的最大回撤（MDD）设定下架阈值，通常为1-2倍MDD（建议1.5-2倍，预留弹性空间）。 <ul><li>例：若策略回测最大回撤为1.93%，设定2倍MDD为下架标准，则实盘回撤达3.86%时下架。</li></ul></li><li><strong>优势</strong>：标准明确、执行果断，避免主观犹豫。</li></ul><h4 id="_2-停滞期判断法" tabindex="-1">2. <strong>停滞期判断法</strong> <a class="header-anchor" href="#_2-停滞期判断法" aria-label="Permalink to &quot;2. **停滞期判断法**&quot;">​</a></h4><ul><li><strong>核心指标</strong>：策略未创新高的持续时间（如回测显示最长252天未创新高）。</li><li><strong>操作</strong>：实盘若达到或超过该停滞期，视为失效并下架。</li></ul><p><img src="'+e+'" alt=""></p><h4 id="_3-权益曲线均线法" tabindex="-1">3. <strong>权益曲线均线法</strong> <a class="header-anchor" href="#_3-权益曲线均线法" aria-label="Permalink to &quot;3. **权益曲线均线法**&quot;">​</a></h4><ul><li><strong>参考指标</strong>：实盘资金曲线与移动平均线（如25MA）或布林带下轨的位置关系。 <ul><li>当资金曲线跌破MA或布林带下轨时，暂停策略： <ul><li>若后续回撤达到2倍MDD，永久下架；</li><li>若重新突破MA，可考虑重新上架。</li></ul></li></ul></li><li><strong>注意</strong>：策略较多时，建议通过Python等程序自动化监控，避免手动统计繁琐。</li></ul><p><img src="'+i+'" alt=""></p><h4 id="_4-定期绩效筛查法-夏普比率导向" tabindex="-1">4. <strong>定期绩效筛查法（夏普比率导向）</strong> <a class="header-anchor" href="#_4-定期绩效筛查法-夏普比率导向" aria-label="Permalink to &quot;4. **定期绩效筛查法（夏普比率导向）**&quot;">​</a></h4><ul><li><strong>周期</strong>：每月/每周/每季度回测最近一年绩效。</li><li><strong>标准</strong>：若采收率低于0.5（或自定义阈值），说明盈利能力或抗风险能力不足，予以下架。</li></ul><h2 id="二、策略上架管理-从筛选到落地的科学流程" tabindex="-1">二、策略上架管理：从筛选到落地的科学流程 <a class="header-anchor" href="#二、策略上架管理-从筛选到落地的科学流程" aria-label="Permalink to &quot;二、策略上架管理：从筛选到落地的科学流程&quot;">​</a></h2><h3 id="一-明确上架核心指标" tabindex="-1">（一）明确上架核心指标 <a class="header-anchor" href="#一-明确上架核心指标" aria-label="Permalink to &quot;（一）明确上架核心指标&quot;">​</a></h3><ul><li><strong>必选标准</strong>（例）： <ul><li>蒙特卡罗模拟后夏普比率&gt;2；</li><li>年回报率、盈利因子、胜率等指标达标。</li></ul></li><li><strong>禁忌</strong>：禁止凭主观喜好（如曲线“顺眼”）决定上架，标准需固定且可量化。</li></ul><p><img src="'+n+'" alt=""></p><h3 id="二-上架时机选择" tabindex="-1">（二）上架时机选择 <a class="header-anchor" href="#二-上架时机选择" aria-label="Permalink to &quot;（二）上架时机选择&quot;">​</a></h3><h4 id="_1-逆向布局法-基于mdd-权益曲线" tabindex="-1">1. <strong>逆向布局法（基于MDD/权益曲线）</strong> <a class="header-anchor" href="#_1-逆向布局法-基于mdd-权益曲线" aria-label="Permalink to &quot;1. **逆向布局法（基于MDD/权益曲线）**&quot;">​</a></h4><ul><li>等待策略回撤至1.5倍MDD或权益曲线贴近均线/布林带下轨时进场，以降低试错成本。</li><li><em>例</em>：原下架标准为2倍MDD，在1.5倍时进场，若策略失效，仅承担0.5倍MDD损失。</li></ul><h4 id="_2-模拟盘-实盘渐进测试" tabindex="-1">2. <strong>模拟盘-实盘渐进测试</strong> <a class="header-anchor" href="#_2-模拟盘-实盘渐进测试" aria-label="Permalink to &quot;2. **模拟盘-实盘渐进测试**&quot;">​</a></h4><ol><li><strong>模拟盘观察（6个月）</strong>：验证胜率、交易笔数、盈亏比是否与回测一致，筛除过拟合策略。</li><li><strong>轻仓实盘测试（1-2个月）</strong>：考察点差、网络延迟等实盘成本对绩效的影响，若符合预期，再正式上线。</li></ol><h3 id="三-上线前必做准备" tabindex="-1">（三）上线前必做准备 <a class="header-anchor" href="#三-上线前必做准备" aria-label="Permalink to &quot;（三）上线前必做准备&quot;">​</a></h3><ol><li><strong>投资理念一致性</strong>：确保理解并认可策略原理，避免执行中频繁干预。</li><li><strong>资金管理规划</strong>： <ul><li>设定单策略最大投入资金（如1万U），结合下架标准计算可承受最大亏损（如20%MDD对应2000U），若超出风险承受力，减少投入。</li></ul></li><li><strong>小资金实盘预演</strong>：通过真实市场环境测试策略应对能力，暴露潜在问题。</li></ol><h2 id="三、策略运行期管理-动态排序与资金分配" tabindex="-1">三、策略运行期管理：动态排序与资金分配 <a class="header-anchor" href="#三、策略运行期管理-动态排序与资金分配" aria-label="Permalink to &quot;三、策略运行期管理：动态排序与资金分配&quot;">​</a></h2><h3 id="一-定期绩效排序-例-每周-月-季度" tabindex="-1">（一）定期绩效排序（例：每周/月/季度） <a class="header-anchor" href="#一-定期绩效排序-例-每周-月-季度" aria-label="Permalink to &quot;（一）定期绩效排序（例：每周/月/季度）&quot;">​</a></h3><ul><li><strong>指标</strong>：净利润、最大回撤、夏普比率、盈利因子等。</li><li><strong>操作</strong>：对所有策略（如30个）按近期绩效排序，选取前20%表现最优者，按排名分配资金（如排名第一策略资金占比不超过10%，分散风险）。</li></ul><h3 id="二-本质与风险提示" tabindex="-1">（二）本质与风险提示 <a class="header-anchor" href="#二-本质与风险提示" aria-label="Permalink to &quot;（二）本质与风险提示&quot;">​</a></h3><ul><li><strong>核心逻辑</strong>：通过“追优汰劣”动态调整组合，适应变化的市场环境。</li><li><strong>注意</strong>：避免过度依赖排序（类似追涨杀跌），需结合策略周期特性（如不同策略适应不同市场阶段），核心仍在于开发优质策略。</li></ul>',31)]))}const b=t(s,[["render",h]]);export{q as __pageData,b as default};
