import{_ as i,c as s,aI as t,o}from"./chunks/framework.b7dag8kZ.js";const e="/assets/1.DmwOIzbJ.png",a="/assets/2.CNE8uKmM.png",r="/assets/3._2poB-rU.png",n="/assets/4.qOXz8wJC.png",d="/assets/5.tmooYE5o.png",c="/assets/6.DsbOT8Kh.png",b=JSON.parse('{"title":"开发环境IDE配置","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/mt5_quick_start/welcome/mt5_ide"}],["meta",{"property":"og:title","content":"开发环境IDE配置"}]]},"headers":[],"relativePath":"mt5_quick_start/welcome/mt5_ide.md","filePath":"mt5_quick_start/welcome/mt5_ide.md","lastUpdated":1745411614000}'),p={name:"mt5_quick_start/welcome/mt5_ide.md"};function g(h,l,k,u,m,_){return o(),s("div",null,l[0]||(l[0]=[t('<h1 id="开发环境ide配置" tabindex="-1">开发环境IDE配置 <a class="header-anchor" href="#开发环境ide配置" aria-label="Permalink to &quot;开发环境IDE配置&quot;">​</a></h1><p><strong>MT5开发环境IDE（MetaEditor）的配置</strong>。开发MT5的EA时，我们使用的是集成开发环境 <strong>MetaEditor</strong>，集成了代码编辑、调试、编译等全套工具。</p><p>按F4 或者 菜单栏下方点击<code>IDE</code>图标 进入IDE界面。</p><h2 id="一、metaeditor核心功能区域" tabindex="-1">一、MetaEditor核心功能区域 <a class="header-anchor" href="#一、metaeditor核心功能区域" aria-label="Permalink to &quot;一、MetaEditor核心功能区域&quot;">​</a></h2><ol><li><p><strong>导航器（左侧面板）</strong></p><ul><li>存储代码文件，结构与MT5数据文件夹一致： <ul><li><code>Experts</code>：存放EA源码（<code>.mq5</code>）和编译后的执行文件（<code>.ex5</code>）</li><li><code>Indicators</code>：存放自定义指标</li><li><code>Scripts</code>：存放脚本文件</li><li><code>Include</code>：存放自定义库文件（后续进阶使用）</li></ul></li></ul></li><li><p><strong>代码编辑器（中央主窗口）</strong></p><ul><li>主要工作区域，用于编写MQL5代码，支持语法高亮、自动补全等功能。</li></ul></li><li><p><strong>工具栏（顶部功能区）</strong></p><ul><li><strong>新建文档</strong>：创建新的EA、指标或脚本文件</li><li><strong>保存</strong>：保存当前编辑的文件（快捷键：<code>Ctrl+S</code>）</li><li><strong>编译</strong>（原翻译“编写”）：将<code>.mq5</code>源码转换为<code>.ex5</code>可执行文件（快捷键：<code>F7</code>）</li><li><strong>调试</strong>：启动调试模式，排查代码逻辑错误</li></ul></li></ol><p><img src="'+e+'" alt=""></p><h2 id="二、编译流程详解" tabindex="-1">二、编译流程详解 <a class="header-anchor" href="#二、编译流程详解" aria-label="Permalink to &quot;二、编译流程详解&quot;">​</a></h2><ol><li><p><strong>编译的作用</strong></p><ul><li>MQL5代码需通过编译生成<code>.ex5</code>文件，才能在MT5中运行。</li><li>编译时自动检查语法错误，错误信息显示在下方<strong>输出窗口</strong>。</li></ul></li><li><p><strong>编译操作技巧</strong></p><ul><li><strong>实时编译</strong>：每写一行或一段代码后按<code>F7</code>，即时定位错误（避免批量编写后难以排查）。</li><li><strong>输出窗口</strong>：显示编译结果（<code>0 errors, 0 warnings</code>表示无异常），错误行号会高亮提示。</li><li><strong>保存与编译联动</strong>：修改代码后编译会自动保存文件（文件未保存时标题栏显示<code>*</code>标记）。</li></ul></li></ol><h2 id="三、代码风格与环境配置" tabindex="-1">三、代码风格与环境配置 <a class="header-anchor" href="#三、代码风格与环境配置" aria-label="Permalink to &quot;三、代码风格与环境配置&quot;">​</a></h2><ol><li><strong>设置代码显示样式</strong><ul><li>路径：<code>工具→选项→编辑器</code><ul><li><strong>行号显示</strong>：勾选“显示行号”（便于定位代码位置）</li><li><strong>字体与字号</strong>：推荐<code>Consolas 12号</code>（清晰易读）</li><li><strong>代码风格</strong>：选择<code>Java风格</code>（花括号换行格式）</li></ul></li></ul></li></ol><p><img src="'+a+'" alt=""></p><ol start="2"><li><strong>格式化代码</strong><ul><li>编写代码后，点击工具栏<code>样式</code>按钮，自动调整代码缩进和格式，提升可读性。</li></ul></li></ol><p><img src="'+r+'" alt=""></p><h2 id="四、hello-world示例-第一个ea开发" tabindex="-1">四、Hello World示例：第一个EA开发 <a class="header-anchor" href="#四、hello-world示例-第一个ea开发" aria-label="Permalink to &quot;四、Hello World示例：第一个EA开发&quot;">​</a></h2><ol><li><strong>新建EA文件</strong><ul><li>步骤： <ol><li>点击工具栏<code>新建</code>→选择“EA交易(模板)”</li><li>输入名称（如<code>HelloWorld</code>），作者和链接可自定义</li><li>勾选“创建示例”（可选，新手建议取消以编写纯代码）</li></ol></li></ul></li></ol><p><img src="'+n+`" alt=""></p><ol start="2"><li><p><strong>编写代码</strong><br> 在<code>OnInit()</code>函数中添加弹窗代码：</p><div class="language-cpp vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">cpp</span><pre class="shiki shiki-themes github-light github-dark vp-code" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">int</span><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;"> OnInit</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">()  </span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">{  </span></span>
<span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">    Alert</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;Hello, World!&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">);</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">  // 弹出提示框  </span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">}</span></span></code></pre></div></li><li><p><strong>编译与运行</strong></p><ul><li>按<code>F7</code>编译，确保输出窗口无错误</li><li>启用自动交易（点击工具栏绿色三角按钮）</li><li>打开MT5，在导航栏<code>Experts</code>找到<code>HelloWorld.ex5</code>，拖曳到任意图表 EA运行时会弹出“Hello, World!”提示框</li></ul></li></ol><p><img src="`+d+'" alt=""></p><p><img src="'+c+'" alt=""></p>',19)]))}const q=i(p,[["render",g]]);export{b as __pageData,q as default};
