import{_ as r,c as o,ag as s,o as a}from"./chunks/framework.CCnnzLsu.js";const l="/assets/1.vzOk9Nnp.png",e="/assets/3.BsQWYqcE.png",i="/assets/4.lJ6TuxGL.png",n="/assets/5.DBgNISQM.png",c="/assets/6.CONjt0UZ.png",g="/assets/7.C1mbDNVD.png",d="/assets/8.CqfyQabY.png",p="/assets/9.DGW4lEXY.png",u="/assets/10.7bPBfgEx.png",h="/assets/11.D5h6fJiO.png",m="/assets/12.CJJdKTWc.png",P=JSON.parse('{"title":"认识MT5","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/mt5_quick_start/welcome/mt5_indroduce"}],["meta",{"property":"og:title","content":"认识MT5"}]]},"headers":[],"relativePath":"mt5_quick_start/welcome/mt5_indroduce.md","filePath":"mt5_quick_start/welcome/mt5_indroduce.md","lastUpdated":1745411614000}'),_={name:"mt5_quick_start/welcome/mt5_indroduce.md"};function q(b,t,f,k,x,M){return a(),o("div",null,t[0]||(t[0]=[s('<h1 id="认识mt5" tabindex="-1">认识MT5 <a class="header-anchor" href="#认识mt5" aria-label="Permalink to &quot;认识MT5&quot;">​</a></h1><p>访问<a href="https://www.metatrader5.com" target="_blank" rel="noreferrer">https://www.metatrader5.com</a> 下载MT5客户端</p><p><img src="'+l+'" alt=""></p><h2 id="一、mt5界面核心功能解析" tabindex="-1">一、MT5界面核心功能解析 <a class="header-anchor" href="#一、mt5界面核心功能解析" aria-label="Permalink to &quot;一、MT5界面核心功能解析&quot;">​</a></h2><h3 id="_1-市场报价窗口" tabindex="-1">1. <strong>市场报价窗口</strong> <a class="header-anchor" href="#_1-市场报价窗口" aria-label="Permalink to &quot;1. **市场报价窗口**&quot;">​</a></h3><ul><li><strong>常用列设置</strong>： <ul><li><p>右键点击列标题，勾选<strong>点差</strong>（手动交易辅助判断开仓时机，量化交易可忽略）。</p></li><li><p>右键→<strong>“规格”</strong>：查看交易品种的<strong>最小手数</strong>（影响代码中仓位计算）、<strong>交易时段</strong>等关键参数。</p></li></ul></li></ul><p><img src="'+e+'" alt=""></p><p><img src="'+i+'" alt=""></p><h3 id="_2-导航栏" tabindex="-1">2. <strong>导航栏</strong> <a class="header-anchor" href="#_2-导航栏" aria-label="Permalink to &quot;2. **导航栏**&quot;">​</a></h3><ul><li><strong>账号管理</strong>：登录Demo账户或实盘账户。</li><li><strong>文件管理</strong>： <ul><li>自定义的<strong>EA、指标、脚本</strong>编译后会自动显示在此处，支持直接拖曳调用。</li></ul></li></ul><p><img src="'+n+'" alt=""></p><h3 id="_3-工具箱" tabindex="-1">3. <strong>工具箱</strong> <a class="header-anchor" href="#_3-工具箱" aria-label="Permalink to &quot;3. **工具箱**&quot;">​</a></h3><ul><li><strong>持仓与订单</strong>：显示当前持仓、历史订单（包括市价单、限价单、突破单等）。</li><li><strong>日志与专家</strong>： <ul><li><strong>日志</strong>：记录MT5运行中的错误信息和调试日志。</li><li><strong>专家（EA）</strong>：显示自定义EA的控制台输出信息（需EA代码中包含日志功能）。</li></ul></li></ul><p><img src="'+c+'" alt=""></p><h2 id="二、图表设置与模板管理" tabindex="-1">二、图表设置与模板管理 <a class="header-anchor" href="#二、图表设置与模板管理" aria-label="Permalink to &quot;二、图表设置与模板管理&quot;">​</a></h2><h3 id="_1-图表个性化配置" tabindex="-1">1. <strong>图表个性化配置</strong> <a class="header-anchor" href="#_1-图表个性化配置" aria-label="Permalink to &quot;1. **图表个性化配置**&quot;">​</a></h3><ul><li><strong>修改显示风格</strong>： <ul><li>右键图表→<strong>“属性”</strong>：选择**“Black on White”**（黑底白字）或自定义配色，关闭网格线。</li></ul></li><li><strong>保存模板</strong>： <ul><li>右键→<strong>“模板”→“保存模板”</strong>：命名后设为默认模板（如“Default”），新开图表自动应用。</li></ul></li></ul><p><img src="'+g+'" alt=""></p><h3 id="_2-时间周期选择" tabindex="-1">2. <strong>时间周期选择</strong> <a class="header-anchor" href="#_2-时间周期选择" aria-label="Permalink to &quot;2. **时间周期选择**&quot;">​</a></h3><ul><li>MT5支持更细分的周期（如M2、M3、H2、H3），量化交易中可利用非标准周期分散滑点风险。</li><li>路径：时间周期栏→<strong>“自定义”</strong>，添加常用周期。</li></ul><p><img src="'+d+'" alt=""></p><h2 id="三、文件目录与第三方工具挂载" tabindex="-1">三、文件目录与第三方工具挂载 <a class="header-anchor" href="#三、文件目录与第三方工具挂载" aria-label="Permalink to &quot;三、文件目录与第三方工具挂载&quot;">​</a></h2><p>MT5数据文件夹路径：<br><code>文件→打开数据文件夹→MQL5</code></p><ul><li><strong>EA（Expert Advisors）</strong>：存放<code>.mq5</code>（源码）或<code>.ex5</code>（编译后）文件，路径：<code>MQL5/Experts/</code></li><li><strong>指标（Indicators）</strong>：存放自定义指标文件，路径：<code>MQL5/Indicators/</code></li><li><strong>脚本（Scripts）</strong>：存放自动化脚本，路径：<code>MQL5/Scripts/</code></li></ul><p><strong>使用步骤</strong>：</p><ol><li>将文件放入对应目录，重启MT5或右键导航栏→<strong>刷新</strong>。</li><li>双击或拖曳文件到图表窗口即可调用。</li></ol><h2 id="四、ea运行关键设置" tabindex="-1">四、EA运行关键设置 <a class="header-anchor" href="#四、ea运行关键设置" aria-label="Permalink to &quot;四、EA运行关键设置&quot;">​</a></h2><ol><li><p><strong>启用算法交易</strong>：</p><ul><li>点击工具栏<strong>自动交易按钮</strong>（红色→绿色），确保<strong>允许算法交易</strong>勾选。</li><li>未勾选时，EA无法下单，日志会提示：<code>Automated trading is disabled</code>。</li></ul></li><li><p><strong>参数配置</strong>：</p><ul><li>拖曳EA到图表后，弹出输入面板，按需设置暴露的参数（如止损、止盈、资金比例等）。</li></ul></li></ol><p><img src="'+p+'" alt=""></p><h2 id="五、脚本与指标的快速调用" tabindex="-1">五、脚本与指标的快速调用 <a class="header-anchor" href="#五、脚本与指标的快速调用" aria-label="Permalink to &quot;五、脚本与指标的快速调用&quot;">​</a></h2><ul><li><strong>脚本</strong>： <ul><li>功能：执行一次性任务（如获取账户信息、批量平仓）。</li><li>使用：拖曳脚本到图表，自动运行并显示结果（如账户杠杆、净盈利、保证金等）。</li></ul></li></ul><p><img src="'+u+'" alt=""></p><ul><li><strong>指标</strong>： <ul><li>系统自带指标（如MA、MACD、RSI）可直接调用，第三方指标按上述目录存放后拖曳使用。</li></ul></li></ul><p><img src="'+h+'" alt=""></p><h2 id="六、后续重点-策略测试器" tabindex="-1">六、后续重点：策略测试器 <a class="header-anchor" href="#六、后续重点-策略测试器" aria-label="Permalink to &quot;六、后续重点：策略测试器&quot;">​</a></h2><p>本次未详细讲解的<strong>策略测试器</strong>是量化交易的核心工具，可快速回测策略在历史数据中的表现（胜率、回撤、盈亏比等）。后续将深入解析其功能与使用方法。</p><p><img src="'+m+'" alt=""></p>',37)]))}const A=r(_,[["render",q]]);export{P as __pageData,A as default};
