import{_ as d,c as e,aI as r,o}from"./chunks/framework.b7dag8kZ.js";const b=JSON.parse('{"title":"模型打分公式","description":"","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://vite.dev/strategy_square/indicator_desc/model_score"}],["meta",{"property":"og:title","content":"模型打分公式"}]]},"headers":[],"relativePath":"strategy_square/indicator_desc/model_score.md","filePath":"strategy_square/indicator_desc/model_score.md","lastUpdated":null}'),a={name:"strategy_square/indicator_desc/model_score.md"};function l(i,t,c,h,s,n){return o(),e("div",null,t[0]||(t[0]=[r('<h1 id="模型打分公式" tabindex="-1">模型打分公式 <a class="header-anchor" href="#模型打分公式" aria-label="Permalink to &quot;模型打分公式&quot;">​</a></h1><h2 id="盈利能力" tabindex="-1">盈利能力 <a class="header-anchor" href="#盈利能力" aria-label="Permalink to &quot;盈利能力&quot;">​</a></h2><table tabindex="0"><thead><tr><th><code>A</code>-回测数值</th><th>打分</th><th><code>B1</code>-回测数值</th><th>打分</th><th><code>B2</code>-回测数值</th><th>打分</th></tr></thead><tbody><tr><td>&lt;10%</td><td>0</td><td>&lt;10%</td><td>0</td><td>&lt;10%</td><td>0</td></tr><tr><td>10--20%</td><td>30</td><td>10--20%</td><td>30</td><td>10--20%</td><td>30</td></tr><tr><td>20--50%</td><td>60</td><td>20--50%</td><td>60</td><td>20--50%</td><td>60</td></tr><tr><td>50--80%</td><td>80</td><td>50--80%</td><td>80</td><td>50--80%</td><td>80</td></tr><tr><td>80%</td><td>100</td><td>80%</td><td>100</td><td>80%</td><td>100</td></tr></tbody></table><ul><li><code>A</code> 初始资金按50%折算年化收益率,50%是指初始持仓比例。初始持仓不足或高于50%，调整为50%</li><li><code>B1</code> 15分钟及以上周期，近一年，初始资金按50%折算年化收益率,从测试结束时间向前取一年的收益率，不足一年折算为年化收益率</li><li><code>B2</code> 15分钟以下周期，近半年，初始资金按50%折算年化收益率,从测试结束时间向前取6个月的收益率，折算成年化收益率。不足6个月以实际时间长短折算为年化收益率</li></ul><h2 id="稳定性" tabindex="-1">稳定性 <a class="header-anchor" href="#稳定性" aria-label="Permalink to &quot;稳定性&quot;">​</a></h2><table tabindex="0"><thead><tr><th><code>C</code>-胜率</th><th><code>C</code></th><th><code>D</code>-盈亏比</th><th><code>D</code></th></tr></thead><tbody><tr><td>&lt;30%</td><td>30</td><td>&lt;0.5</td><td>0</td></tr><tr><td>30--40%</td><td>50</td><td>0.5--1</td><td>20</td></tr><tr><td>40--50%</td><td>60</td><td>1--1.5</td><td>40</td></tr><tr><td>50--60%</td><td>80</td><td>1.5--2</td><td>50</td></tr><tr><td>60--70%</td><td>90</td><td>2--3</td><td>60</td></tr><tr><td>&gt;70%</td><td>100</td><td>3--3.5</td><td>70</td></tr><tr><td>—</td><td>—</td><td>3.5--5</td><td>80</td></tr><tr><td>—</td><td>—</td><td>&gt;5</td><td>100</td></tr></tbody></table><ul><li><code>C</code>测试报告-胜率；</li><li><code>D</code>测试报告-平均盈利/平均亏损。</li></ul><h2 id="风险暴露" tabindex="-1">风险暴露 <a class="header-anchor" href="#风险暴露" aria-label="Permalink to &quot;风险暴露&quot;">​</a></h2><table tabindex="0"><thead><tr><th><code>E</code>-权益最大回撤比</th><th><code>E</code></th><th><code>F</code>-每手最大亏损额/占用保证金</th><th><code>E</code></th></tr></thead><tbody><tr><td>&gt;50%</td><td>20</td><td>&gt;0.5</td><td>0</td></tr><tr><td>30--50%</td><td>40</td><td>0.4--0.5</td><td>30</td></tr><tr><td>20--30%</td><td>50</td><td>0.3--0.4</td><td>40</td></tr><tr><td>15--20%</td><td>60</td><td>0.25--0.3</td><td>50</td></tr><tr><td>10--15%</td><td>70</td><td>0.2--0.25</td><td>60</td></tr><tr><td>5--10%</td><td>80</td><td>0.1--0.2</td><td>80</td></tr><tr><td>&lt;5%</td><td>100</td><td>&lt;0.1</td><td>100</td></tr></tbody></table><ul><li><code>E</code>测试报告-权益最大回撤比；</li><li><code>F</code>测试报告-每手最大亏损； <ul><li>占用保证金：出现每手最大亏损额时交易每手占用保证金。</li></ul></li></ul><h2 id="敏感度" tabindex="-1">敏感度 <a class="header-anchor" href="#敏感度" aria-label="Permalink to &quot;敏感度&quot;">​</a></h2><table tabindex="0"><thead><tr><th><code>G1</code>-15分钟及以上周期，平均交易周期</th><th>打分</th><th><code>G2</code>-15分钟以下周期，日均交易次数</th><th>打分</th><th><code>H</code>-每手平均盈利点数</th><th>打分</th></tr></thead><tbody><tr><td>&lt;3</td><td>10</td><td></td><td></td><td>1--5</td><td>0</td></tr><tr><td>3--10</td><td>30</td><td>&gt;5</td><td>40</td><td>5--10</td><td>20</td></tr><tr><td>10--20</td><td>50</td><td>&lt;1</td><td>40</td><td>10--20</td><td>40</td></tr><tr><td>20-30</td><td>60</td><td>1--2</td><td>50</td><td>20--30</td><td>60</td></tr><tr><td>30--50</td><td>80</td><td>3--5</td><td>60</td><td>30--40</td><td>80</td></tr><tr><td>&gt;50</td><td>100</td><td>2--3</td><td>80</td><td>&gt;40</td><td>100</td></tr></tbody></table><ul><li><code>G1</code>-测试报告-平均交易周期；</li><li><code>G2</code>-总的交易次数/测试时间段交易日数；</li><li><code>H</code>-测试报告-每手平均盈亏/交易单位，如豆粕每手平均盈亏为300元，每手平均盈利点数为300/10（交易单位）=30点。</li></ul><h2 id="模型打分公式-1" tabindex="-1">模型打分公式 <a class="header-anchor" href="#模型打分公式-1" aria-label="Permalink to &quot;模型打分公式&quot;">​</a></h2><p>分为加载在15分钟及以上周期的模型和加载在15分钟以下周期模型，按照加载的周期选择不同的计算公式。</p><ul><li><strong>15分钟及以上周期模型</strong>：<br> \\[ 0.25 \\times (0.5A + 0.5B1) + 0.25 \\times (0.5C + 0.5D) + 0.25 \\times (0.5E + 0.5F) + 0.25 \\times (0.5G1 + 0.5H) \\]</li><li><strong>15分钟以下周期模型</strong>： \\[ 0.25 \\times (0.5A + 0.5B2) + 0.25 \\times (0.5C + 0.5D) + 0.25 \\times (0.5E + 0.5F) + 0.25 \\times (0.5G2 + 0.5H) \\]</li></ul><p><strong>示例</strong>：<br> 一个模型加载在15分钟周期，A为60，B1为30，C为50，D为60，E为40，F为50，G1为50，H为60，那么模型打分等于：<br> \\[ 0.25 \\times (0.5 \\times 60 + 0.5 \\times 30) + 0.25 \\times (0.5 \\times 50 + 0.5 \\times 60) + 0.25 \\times (0.5 \\times 40 + 0.5 \\times 50) + 0.25 \\times (0.5 \\times 50 + 0.5 \\times 60) = 50 \\]</p>',17)]))}const u=d(a,[["render",l]]);export{b as __pageData,u as default};
